import { Fragment } from 'react';
import { useContext } from 'react';
import icon from '../../Assets/carts.png'
import CartContext from '../../Store/cart-context';
import classes from './HeaderCartButton.module.css'
const HeaderCartButton = props => {
    const contextctx = useContext(CartContext);
    const numberOfCartItems=contextctx.items.reduce((curNumber,item)=>{
        return curNumber +item.amount
    },0)
    return (
        <Fragment>
            <button className={classes.button} onClick={props.onClickShown}>
        <span><img className={classes.icon} src={icon} alt="cart" /></span>
        <span className={classes.text}>Your cart</span>
        <span className={classes.badge}> {numberOfCartItems}</span>
        </button>
        </Fragment>
    )
}
export default HeaderCartButton
